import React from 'react';
// Pastikan semua file ini sudah ada di folder src Anda
import LandingPage from './LandingPage';
import ProfilUMKM from './ProfilUMKM';
import Katalog from './Katalog';
import Kontak from './Kontak';

function App() {
  // Fungsi untuk scroll otomatis saat menu diklik (Opsional tapi bagus)
  const scrollToSection = (id) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className="min-h-screen bg-white font-sans text-gray-800">
      {/* NAVBAR */}
      <nav className="flex justify-between items-center px-10 py-4 border-b border-gray-100 sticky top-0 bg-white/90 backdrop-blur-sm z-50">
        <div className="flex items-center gap-2">
          <div className="w-10 h-10 bg-[#e5e1da] rounded-full flex items-center justify-center overflow-hidden text-[8px] italic font-bold text-gray-500">
            LOGO
          </div>
          <span className="font-semibold text-gray-500 tracking-tight text-xl">Scenthusiasm</span>
        </div>
        
        <ul className="flex gap-8 text-sm font-bold">
          <li onClick={() => scrollToSection('beranda')} className="cursor-pointer text-gray-400 hover:text-purple-600 transition">Beranda</li>
          <li onClick={() => scrollToSection('profil')} className="cursor-pointer text-gray-400 hover:text-purple-600 transition">Profile UMKM</li>
          <li onClick={() => scrollToSection('katalog')} className="cursor-pointer text-gray-400 hover:text-purple-600 transition">Produk/Katalog</li>
          <li onClick={() => scrollToSection('kontak')} className="cursor-pointer text-gray-400 hover:text-purple-600 transition">Kontak</li>
        </ul>
      </nav>

      {/* SEMUA HALAMAN DIGABUNG DI SINI */}
      <main>
        <div id="beranda">
          <LandingPage />
        </div>
        
        <div id="profil">
          <ProfilUMKM />
        </div>
        
        <div id="katalog">
          <Katalog />
        </div>
        
        <div id="kontak">
          <Kontak />
        </div>
      </main>

      {/* FOOTER SEDERHANA */}
      <footer className="py-6 text-center text-gray-400 text-xs border-t border-gray-100">
        &copy; 2026 Scenthusiasm Pekanbaru. All Rights Reserved.
      </footer>
    </div>
  );
}

export default App;
