import React, { useState } from 'react';
import { Instagram, Facebook, Clock, MapPin, MessageCircle, ShoppingBag } from 'lucide-react';
import LandingPage from './LandingPage';
import ProfilUMKM from './ProfilUMKM';
import Katalog from './Katalog';
import Kontak from './Kontak';

function App() {
  const [activePage, setActivePage] = useState('Beranda');

  return (
    <div className="min-h-screen bg-white font-sans text-gray-800">
      {/* NAVBAR TETAP */}
      <nav className="flex justify-between items-center px-10 py-4 border-b border-gray-100 sticky top-0 bg-white z-50">
        <div className="flex items-center gap-2">
          <div className="w-10 h-10 bg-[#e5e1da] rounded-full flex items-center justify-center overflow-hidden">
            <span className="text-[8px] text-gray-500 italic font-bold">LOGO</span>
          </div>
          <span className="font-semibold text-gray-400 tracking-tight text-xl">Scenthusiasm</span>
        </div>
        <ul className="flex gap-8 text-sm font-bold">
          {['Beranda', 'Profile UMKM', 'Produk/Katalog', 'Kontak'].map((item) => (
            <li 
              key={item}
              onClick={() => setActivePage(item)}
              className={`cursor-pointer pb-1 transition-all ${
                activePage === item 
                ? 'text-purple-600 border-b-4 border-purple-600' 
                : 'text-gray-400 hover:text-purple-400'
              }`}
            >
              {item}
            </li>
          ))}
        </ul>
      </nav>

      {/* RENDER HALAMAN BERDASARKAN NAVIGASI */}
      <main>
        {activePage === 'Beranda' && <LandingPage />}
        {activePage === 'Profile UMKM' && <ProfilUMKM />}
        {activePage === 'Produk/Katalog' && <Katalog />}
        {activePage === 'Kontak' && <Kontak />}
      </main>

      {/* FOOTER (Hanya muncul jika bukan di halaman Kontak untuk menghindari duplikasi) */}
      {activePage !== 'Kontak' && (
        <footer className="px-10 py-12 bg-white border-t border-gray-100">
          <div className="flex items-center gap-4 mb-4">
            <h3 className="text-xl font-bold text-purple-600">Hubungi <span className="text-orange-400">Kami</span></h3>
            <div className="flex gap-3">
              <Instagram size={24} className="text-pink-500 cursor-pointer" />
              <Facebook size={24} className="text-blue-700 cursor-pointer" />
              <MessageCircle size={24} className="text-green-500 cursor-pointer" />
              <ShoppingBag size={24} className="text-orange-600 cursor-pointer" />
            </div>
          </div>
          <p className="text-xs text-gray-400 mb-6 max-w-sm">
            Hubungi kami melalui <span className="text-orange-400">WhatsApp</span> kami siap melayani Anda sesuai jam kerja.
          </p>
          <div className="space-y-3 text-sm text-gray-500">
            <div className="flex items-center gap-2">
              <Clock size={16} className="text-orange-400" /> Senin-Jumat - 08.00- 17.00
            </div>
            <div className="flex items-center gap-2">
              <MapPin size={16} className="text-orange-400" /> Pekanbaru, Riau
            </div>
          </div>
        </footer>
      )}
    </div>
  );
}

export default App;
