import React from "react";

/* =======================
   IMPORT GAMBAR PRODUK
======================= */
import diorImg from "./assets/sauvage.png";
import whiskeyImg from "./assets/whiskey.png";
import ckImg from "./assets/defy 1.png";
import elvictoImg from "./assets/escobar (1) (1) 1.png";
import dieselImg from "./assets/Diesel (1) (1) 1.png";
import yslImg from "./assets/Yve (1) (1) 1.png";
import armaniImg from "./assets/armani (3) 1.png";
import alienGoddessImg from "./assets/AlienGoddes 1.png";
import jpgImg from "./assets/JeanPaul (3) 1.png";
import alienMuglerImg from "./assets/AlienMugler 1.png";

/* =======================
   DATA PRODUK
======================= */
const products = [
  {
    id: 1,
    brand: "Dior",
    name: "Sauvage",
    image: diorImg,
    price: "Rp125.000 - Rp225.000",
    link: "https://shopee.co.id/scenthusiasm/dior-sauvage",
    topNotes: "Calabrian bergamot, Pepper",
    middleNotes:
      "Sichuan Pepper, Lavender, Pink Pepper, Vetiver, Patchouli, Geranium, Elemi",
    baseNotes: "Ambroxan, Cedar, Labdanum",
  },
  {
    id: 2,
    brand: "Whiskey",
    name: "Prestigious",
    image: whiskeyImg,
    price: "Rp70.000 - 110.000",
    link:
      "https://shopee.co.id/Decant-Parfume-Limited-Edition-Whiskey-%E2%80%9CPrestigious%E2%80%9D-i.1632412648.47350744747",
    topNotes: "Caramel, Vanilla, Honey",
    middleNotes: "Oud Wood, Cinnamon, Nutmeg",
    baseNotes: "Smokey Oak, Toffee, Malt",
  },
  {
    id: 3,
    brand: "CK",
    name: "DEFY",
    image: ckImg,
    price: "Rp75.000 - Rp130.000",
    link:
      "https://shopee.co.id/Decant-Parfum-Unisex-Fresh-Clean-Type-EDT-i.1632412648.53950747217",
    topNotes: "Pink Pepper",
    middleNotes: "Lavender",
    baseNotes: "Cacao",
  },
  {
    id: 4,
    brand: "Elvicto",
    name: "Escobar",
    image: elvictoImg,
    price: "Rp27.000 - Rp55.000",
    link:
      "https://shopee.co.id/Decant-Parfum-Elvict0-Esconbar(Eau-de-Parfum-for-Men)-i.1632412648.56300703487",
    topNotes: "Bergamot, Lemon, Cardamom",
    middleNotes: "Lavender, Geranium, Cinnamon",
    baseNotes: "Amberwood, Patchouli, Tonka Bean, Vanilla",
  },
  {
    id: 5,
    brand: "Diesel",
    name: "Bad",
    image: dieselImg,
    price: "Rp85.000 - Rp105.000",
    link:
      "https://shopee.co.id/Decant-D1esel-B4d-Parfum-(Eau-de-Toilette-for-Men)-i.1632412648.52750694184",
    topNotes: "Lavender, Bergamot, Cardamom, Violet Leaf",
    middleNotes: "Caviar, Orris Root, Sage",
    baseNotes: "Woody Notes, Tobacco, Ambroxan, Tonka Bean, Patchouli",
  },
  {
    id: 6,
    brand: "YSL",
    name: "Y EDP",
    image: yslImg,
    price: "Rp85.000 - Rp135.000",
    link:
      "https://shopee.co.id/Decant-Parfum-Pria-%E2%80%9CSaint-Y-Signature%E2%80%9D-Eau-de-Parfum-i.1632412648.56700730490",
    topNotes: "Apple, Ginger, Bergamot",
    middleNotes: "Sage, Juniper Berries, Geranium",
    baseNotes: "Amberwood, Tonka Bean, Cedar, Vetiver, Olibanum",
  },
  {
    id: 7,
    brand: "Emporio Armani",
    name: "Stronger With You Intensely",
    image: armaniImg,
    price: "Rp80.000 - Rp135.000",
    link:
      "https://shopee.co.id/Decant-Parfum-Pria-Stronger-Intense-Type-EDP-i.1632412648.56200740714",
    topNotes: "Pink Pepper, Juniper, Violet",
    middleNotes: "Toffee, Cinnamon, Lavender, Sage",
    baseNotes: "Vanilla, Amber, Tonka Bean, Suede",
  },
  {
    id: 8,
    brand: "Alien Goddess Supra",
    name: "Florale Mugler",
    image: alienGoddessImg,
    price: "Rp95.000 - Rp150.000",
    link:
      "https://shopee.co.id/Decant-Parfum-Wanita-Goddess-Floral-Type-EDP-i.1632412648.52400736310",
    topNotes: "Prickly Pear, Cactus Flower",
    middleNotes: "Jasmine Sambac, Immortelle",
    baseNotes: "Amber, Musk, Desert Rose",
  },
  {
    id: 9,
    brand: "Jean Paul Gaultier",
    name: "Le Male Elixir",
    image: jpgImg,
    price: "Rp95.000 - Rp135.000",
    link:
      "https://shopee.co.id/Decan-Parfum-Pl-Elixir-Paul-Original-100--i.1632412648.48800750136",
    topNotes: "Lavender, Mint",
    middleNotes: "Vanilla, Benzoin",
    baseNotes: "Honey, Tonka Bean, Tobacco",
  },
  {
    id: 10,
    brand: "Alien Mugler",
    name: "Original",
    image: alienMuglerImg,
    price: "Rp90.000 - Rp140.000",
    link: "https://shopee.co.id/scenthusiasm/alien-mugler",
    topNotes: "Jasmine Sambac",
    middleNotes: "Cashmeran",
    baseNotes: "Amber",
  },
];

/* =======================
   KOMPONEN KATALOG
======================= */
const Katalog = () => {
  return (
    <section className="min-h-screen bg-purple-50 px-4 py-20">
      <div className="mx-auto mt-40 grid max-w-6xl grid-cols-1 gap-x-12 gap-y-64 md:grid-cols-2">
        {products.map((item) => (
          <div key={item.id} className="relative flex flex-col items-center">
            {/* GAMBAR PRODUK */}
            <div className="pointer-events-none absolute -top-56 z-20 flex w-full justify-center">
              <div className="flex h-80 w-64 items-end justify-center">
                <img
                  src={item.image}
                  alt={item.name}
                  className={`max-h-full max-w-full object-contain drop-shadow-2xl transition-transform duration-300 hover:scale-110
                    ${item.id === 3 ? "scale-[1.35]" : ""}
                    ${item.id === 9 ? "pb-2" : ""}
                  `}
                />
              </div>
            </div>

            {/* KARTU PRODUK */}
            <div className="relative z-10 flex min-h-[350px] w-full flex-col justify-between rounded-[2.5rem] bg-gray-200 p-8 pt-44 shadow-md">
              {/* JUDUL */}
              <div className="mb-6 flex items-start justify-between">
                <h3 className="w-1/3 text-2xl font-bold leading-tight tracking-wide text-purple-600">
                  {item.brand}
                </h3>
                <div className="w-1/3" />
                <h4 className="w-1/3 text-right text-xl font-bold leading-tight text-orange-400">
                  {item.name}
                </h4>
              </div>

              {/* NOTES */}
              <div className="mb-6 space-y-1 px-1 text-[11px] text-gray-700">
                <p>
                  <span className="font-bold text-orange-400">Top Notes:</span>{" "}
                  {item.topNotes}
                </p>
                <p>
                  <span className="font-bold text-orange-400">
                    Middle Notes:
                  </span>{" "}
                  {item.middleNotes}
                </p>
                <p>
                  <span className="font-bold text-orange-400">Base Notes:</span>{" "}
                  {item.baseNotes}
                </p>
              </div>

              {/* HARGA */}
              <div className="mt-auto border-t border-gray-300 pb-2 pt-4 text-center">
                <p className="text-base font-extrabold tracking-wide text-black">
                  {item.price}
                </p>
              </div>
            </div>

            {/* TOMBOL BELI */}
            <a
              href={item.link}
              target="_blank"
              rel="noopener noreferrer"
              className="z-30 -mt-6"
            >
              <button className="transform cursor-pointer rounded-full border-4 border-white bg-orange-400 px-16 py-3 font-bold text-white shadow-lg transition hover:-translate-y-1 hover:bg-orange-500">
                Beli
              </button>
            </a>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Katalog;
