import React, { useState } from 'react';
import { Instagram, Facebook, MessageCircle, Clock, MapPin, Tablet, ShoppingBag } from 'lucide-react';
import LandingPage from './LandingPage';
import ProfilUMKM from './ProfilUMKM';
import Katalog from './Katalog';

function App() {
  const [activePage, setActivePage] = useState('Beranda');

  return (
    <div className="min-h-screen bg-white font-sans text-gray-800">
      {/* NAVBAR */}
      <nav className="flex justify-between items-center px-10 py-4 border-b border-gray-100 sticky top-0 bg-white z-50">
        <div className="flex items-center gap-2">
          <div className="w-10 h-10 bg-gray-200 rounded-full flex items-center justify-center">LOGO</div>
          <span className="font-semibold text-gray-500">Scenthusiasm</span>
        </div>
        <ul className="flex gap-8 text-sm font-medium">
          {['Beranda', 'Profile UMKM', 'Produk/Katalog', 'Kontak'].map((item) => (
            <li 
              key={item}
              onClick={() => setActivePage(item)}
              className={`cursor-pointer pb-1 transition-all ${activePage === item ? 'text-purple-600 border-b-2 border-purple-600' : 'text-gray-400 hover:text-purple-400'}`}
            >
              {item}
            </li>
          ))}
        </ul>
      </nav>

      {/* KONTEN DINAMIS */}
      <main>
        {/* {activePage === 'Beranda' && <LandingPage />} */}
        {activePage === 'Profile UMKM' && <ProfilUMKM />}
        {activePage === 'Produk/Katalog' && <Katalog />}
        {activePage === 'Kontak' && <div className="py-20 text-center text-gray-400 italic">Silakan hubungi kami melalui media sosial di bawah.</div>}
      </main>

      {/* FOOTER TETAP */}
      <footer className="px-10 py-10 bg-white border-t border-gray-100">
        <div className="flex items-center gap-4 mb-4">
          <h3 className="text-xl font-bold text-purple-600">Hubungi <span className="text-orange-400">Kami</span></h3>
          <div className="flex gap-3">
            <Instagram size={24} className="text-pink-500 cursor-pointer" />
            <Facebook size={24} className="text-blue-700 cursor-pointer" />
            <Tablet size={24} className="text-cyan-400 cursor-pointer" />
            <MessageCircle size={24} className="text-green-500 cursor-pointer" />
            <ShoppingBag size={24} className="text-orange-600 cursor-pointer" />
          </div>
        </div>
        <div className="space-y-3 text-sm text-gray-600">
          <div className="flex items-center gap-2"><Clock size={16} className="text-orange-400" /> Senin-Jumat - 08.00- 17.00</div>
          <div className="flex items-center gap-2"><MapPin size={16} className="text-orange-400" /> Pekanbaru, Riau</div>
        </div>
      </footer>
    </div>
  );
}

export default App;
